import {authors, articles, teams} from "./const";

export function getTeamArticlesDescription(teamId) {
    const teamMembersIds = teams.find(team => team.id === teamId).members;
    const teamMembers = authors.filter(author => teamMembersIds.includes(author.id));
    let teamArticles = [];

    const membersWithArticles = teamMembers.map(member => {
        const memberArticles = articles.filter(article => article.authors && article.authors.includes(member.id));
        teamArticles = [...teamArticles, ...memberArticles]; // concat(memberArticles)

        return {
            name: member.name,
            articles: memberArticles,
        };
    });

    return `
        ${membersWithArticles.map(member => `${member.name} wrote ${getArticlesStringFormat(member.articles)} \n`).join('')}
        ----- \n
        Team 'Webscope ${teamId}' co-authored ${[...new Set(teamArticles)].length} out of ${articles.length}.
    `;

    // const parser = new DOMParser();
    // const value = parser.parseFromString(templateString, );

    // return value;
}

function getArticlesStringFormat(articles) {
    // IF not switch
    switch (articles.length) {
        case 0:
            return `0 articles.`;
        case 1:
            return `an article '${articles[0].text}'`;
        case 2:
            return `articles '${articles[0].text}' and '${articles[1].text}'`;
        case articles.length:
            return `articles '${articles[0].text}', '${articles[1].text}' and ${articles.length - 2} more.`;
        default:
            return null;
    }
}

// Result
// Oliver wrote an article 'RxJS and redux-observable'
// Jan wrote articles 'RxJS and redux-observable', 'Firebase' and 1 more.
