import './App.css';

class App extends Component {

  constructor(props) {
    super(props);

    this.state = {
      value: '',
    }
  }

  firstToUpper = () => {
    // These are converting just first letter of the string.

    // 1) Lowercase and uppercase letters are separated from each other just with 32 points in ascii table
    // const asciiCodeForFirstLetter = input.charCodeAt(0);
    // const upperCaseLetterAlternative = String.fromCharCode(asciiCodeForFirstLetter - 32)
    // return upperCaseLetterAlternative + input.slice(1);

    // 2) Using javascript build-in method toUpperCase() as alternative
    // return `${stringToFormat.charAt(0).toUpperCase()}${stringToFormat.slice(1)}`;

    // 3) Using regex as alternative for method charAt()
    // return stringToFormat.replace(/ˆ\w/, c => c.toUpperCase());

    // let result = '';
    // for (let i = 0; i < input.length; ++i) {
    //   let charCode = input.charCodeAt(i);
    //   if (charCode < 65 || charCode > 90) {
    //     // NOT an uppercase ASCII character
    //     // Append the original character
    //     result += input.substr(i, 1);
    //   } else {
    //     // Character in the ['A'..'Z'] range
    //     // Append the lowercase character
    //     result += String.fromCharCode(charCode + 32);
    //   }
    // }
    //
    // return result;
    const splicedValue = this.state.value.split(" ");
    const toUpper = splicedValue.map(word => {
      const value = word.replace(/ˆ\w/, c => {return c.toUpperCase()})
      console.log(word, value);
      return value;
    });
    const joinedValue = toUpper.join(" ");

    console.log(splicedValue, toUpper, joinedValue);
  }

  onInputChange = ({target}) => {
    this.setState({value: target.value})
  }

  render() {
    return (
      <div className="App">
        <input
          value={this.state.value}
          onChange={this.onInputChange}
        />
        <button
          onClick={this.firstToUpper}
        >
          click
        </button>
      </div>
    );
  }
}

export default App;
