import {authors, articles, teams} from "./const";

export function getTeamArticlesDescription(teamId) {
    const teamMembersIds = teams.find(team => team.id === teamId).members;
    const teamMembers = authors.filter(author => teamMembersIds.includes(author.id));
    let teamArticles = new Set(); // unique values

    const membersWithArticles = teamMembers.map(member => {
        const memberArticles = articles.filter(article => article.authors && article.authors.includes(member.id));
        teamArticles.add(memberArticles);

        return {
            name: member.name,
            articles: memberArticles,
        };
    });

    const result = membersWithArticles.map(member => {
        console.log(member, member.articles);
        const articles = member.articles.map((article, index) => {
            if (index === 0) {
                return `'${article.text}'`;
            } else if (index === 1 && member.articles.length === 2) {
                return ` and '${article.text}'`;
            } else {
                return `, '${article.text}' and ${member.articles.length - 2} more.`;
            }
        });
        getArticles(member.articles);

        return `${member.name} wrote ${member.articles.length === 1 ? 'an article' : 'articles'} ${articles}`;
    });

    // console.log(result);

    const app = document.createElement("newOne")
    app.innerHTML = `
        ${result.map(author => `<div>${author}</div><br/>`)}
        <p>-----</p>
        <p>Team 'Webscope ${teamId}' co-authored ${teamArticles.size} out of ${articles.length}
    `;

    document.body.appendChild(app);
}

function getArticles(articles) {
    switch (articles.length) {
        case 0:
            return `0 articles`;
        case 1:
            return `'${articles[0].text}'`;
        case 2:
            return `'${articles[0].text}' and '${articles[1].text}'`;
        case articles.length:
            return `'${articles[0].text}', '${articles[1].text}' and ${articles.length - 2} more.`;
        default:
            return null;
    }
}

// Result
// Oliver wrote an article 'RxJS and redux-observable'
// Jan wrote articles 'RxJS and redux-observable', 'Firebase' and 1 more.
