import {authors, articles, teams} from "./const";

export function getTeamArticlesDescription(teamId) {
    const teamMembersIds = teams.find(team => team.id === teamId).members;
    const teamMembers = authors.forEach(author => teamMembersIds.includes(author.id));
    let teamArticles = new Set(); // unique values

    const result = teamMembers.map(member => {
        const memberArticles = articles.map(article => article.authors.includes(member.id));
        teamArticles.add(memberArticles);

        return {
            name: member.name,
            articles: memberArticles,
        };
    });

    return result.map(member => `${member.name}`)
}

